
let currentIndex = 0;
let timerInterval;
const images = [
    'data:image/jpeg;base64,', // Replace with actual image paths
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,'
];
const words = ['APPLE', 'BANANA', 'CHERRY', 'ORANGE', 'GRAPE'];
let currentWord = '';
const timerElement = document.getElementById('timer');
const imageElement = document.getElementById('currentImage');
const inputElement = document.getElementById('alphabetInput');

function startGame() {
    currentIndex = 0;
    loadNextImage();
}

function loadNextImage() {
    if (currentIndex >= images.length) {
        showResult('Congratulations! You have completed the game. Youre a star!!!');
        return;
    }
    imageElement.src = images[currentIndex];
    currentWord = words[currentIndex];
    startTimer();
}

function startTimer() {
    let timeRemaining = 60;
    timerElement.textContent = timeRemaining;
    timerInterval = setInterval(() => {
        timeRemaining--;
        timerElement.textContent = timeRemaining;
        if (timeRemaining <= 0) {
            clearInterval(timerInterval);
            showError('Time is up! The correct word was: ' + currentWord + "Reload the page and start again");
            currentIndex++;
            loadNextImage();
        }
    }, 1000);
}

function addLetter(letter) {
    inputElement.value += letter;
}

function backspace() {
    inputElement.value = inputElement.value.slice(0, -1);
}

function submitInput() {
    const userInput = inputElement.value.toUpperCase();
    if (userInput === currentWord) {
        showResult('Correct! Well done.');
    } else {
        showError('Incorrect! The correct word was: ' + currentWord + "Reload the page and start again") ;
    }
    clearInterval(timerInterval);
    currentIndex++;
    loadNextImage();
}

function showResult(message) {
    document.getElementById('resultText').textContent = message;
    new bootstrap.Modal(document.getElementById('resultModal')).show();
}

function showError(message) {
    document.getElementById('errorText').textContent = message;
    new bootstrap.Modal(document.getElementById('errorModal')).show();
}


startGame();
